// Type definitions for A-Frame HTML elements when used with JSX
declare namespace JSX {
  interface IntrinsicElements {
    // A-Frame elements
    'a-scene': any;
    'a-entity': any;
    'a-assets': any;
    'a-videosphere': any;
    'a-camera': any;
    'a-cursor': any;
    'a-sky': any;
    'a-sphere': any;
    'a-box': any;
    'a-cylinder': any;
    'a-plane': any;
    'a-light': any;

    // Regular HTML elements
    'div': any;
    'span': any;
    'button': any;
    'video': any;
    'audio': any;
    'img': any;
    'input': any;
    'label': any;
    'p': any;
    'h1': any;
    'h2': any;
    'h3': any;
    'h4': any;
    'h5': any;
    'h6': any;
    'a': any;
    'ul': any;
    'li': any;
    'nav': any;
    'header': any;
    'footer': any;
    'main': any;
    'section': any;
    'article': any;
    'aside': any;
  }
} 